package codoACodo;

public class BucleFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i < 11; i++) {  // declara variable i y la carga con 1, i es menor que 11 entonces suma ++ 1 y lo imprime
			System.out.println("el numero es "+i);    //con + separado las lineas que armo
			
		}
		
	}

}
